CREATE FUNCTION [dbo].[mdpfn_sys_IsInheritedFromMetaClass]
(
	@MetaClassId INT,
	@ParentClassName NVARCHAR(256)
)
RETURNS BIT
AS
BEGIN
    DECLARE @ParentClassId INT
	DECLARE @IsCatalogMetaClass Bit
