	    IF @ParentClassId = 0
	    	RETURN 0
	    SET @IsCatalogMetaClass = (SELECT [dbo].[mdpfn_sys_IsInheritedFromMetaClass](@ParentClassId, @ParentClassName))
	END
	RETURN @IsCatalogMetaClass
END
