		INNER JOIN dbo.CatalogLanguage cl ON w.ObjectId = cl.CatalogId AND w.LanguageName = cl.LanguageCode COLLATE DATABASE_DEFAULT
		
	EXEC [ecfVersionCatalog_Save] @VersionCatalogs = @catalogs, @PublishAction = 1

	IF @SelectOutput = 1
